    p = df[target_col].value_counts() / len(df)
    return 1 - sum(p ** 2)

def gini_conditional(df, discrete_col, target_col):
    ret = 0
    for col in df[discrete_col].unique():
        p = len(df[df[discrete_col] == col]) / len(df)
        ret += p * gini(df[df[discrete_col] == col], target_col)
